import MessageList from "./components/messageList/messageList";
import InputMess from "./components/inputMess/inputMess";
import {useState, useEffect} from "react";


function App() {
    const [messageList, setMessageList] = useState(
        [
            {
                id:1,
                author:'tester',
                text:'im push test text',
                time: '1:30:45'
            },
            {
                id:2,
                author:'tester',
                text:'im push test text2',
                time: '1:30:46'
            }
        ]
    )

    const hanlePusherMessage = () => {
        console.log('push')
        let test =  {
            id:3,
            author:'3333',
            text:'im push test 3',
            time: '1:30:333'
        }
        setMessageList(messageList => [...messageList, test])
    }
    useEffect(()=>{
        console.log(messageList)
    },[messageList])
    return (
        <div className="App">
            <MessageList messageArr={messageList}/>
            <InputMess changeFunc={hanlePusherMessage}/>
        </div>
    );
}

export default App;
