import MessageList from "./components/messageList/messageList";
import InputMess from "./components/inputMess/inputMess";
import {useState, useEffect} from "react";


function App() {
    const [idCount, setIdCount] = useState(3)
    const [messageList, setMessageList] = useState(
        [
            {
                id:1,
                author:'tester',
                text:'im push test text',
                time: '1:30:45'
            },
            {
                id:2,
                author:'tester',
                text:'im push test text2',
                time: '1:30:46'
            }
        ]
    )

    const hanlePusherMessage = (messageItem) => {
        console.log('push: ', messageItem)
        setIdCount((idCount)=>idCount+1)

        let test =  {
            id: idCount,
            author: 'tester',
            text: messageItem,
            time: '11111'
        }
        setMessageList(messageList => [...messageList, test])
    }
    useEffect(()=>{
        console.log(messageList)
    },[messageList])
    return (
        <div className="App">
            <MessageList messageArr={messageList}/>
            <InputMess changeFunc={
                hanlePusherMessage
            }/>
        </div>
    );
}

export default App;
